<code>{{velocity}}
#set($query = "from doc.object(JobArea.JobAreaClass) as job")

#if("$!{request.job}" != "")
   #set($query = "$query where doc.fullName='JobArea.${request.job}'")
#else
  #set($query = "$query where job.status = 1")
#end
#set($jobs = $services.query.xwql($query).execute())

#if("$!{request.job}" == "")
{{html wiki="true"}}
    #foreach($job in $jobs)
        #set($jobDoc = $xwiki.getDocument($job))
        #if("$!{jobDoc.position}" != "" &amp;&amp; "$!{jobDoc.description}" != "")
           * &lt;a href="$doc.getURL("view", "job=${jobDoc.position}")"&gt;$jobDoc.position&lt;/a&gt;
        #end
    #end
{{/html}}
#else 
  #set($jobDoc = $xwiki.getDocument($jobs))
  = $jobDoc.position =
  $jobDoc.description
  #if("$!{request.step}" == "")
    {{html}}
      &lt;a href="$doc.getURL("view", "job=${request.job}&amp;step=apply")"&gt;Apply to this position&lt;/a&gt;
    {{/html}} 
  #else
    {{include document="RecruitmentApp.NewCandidateSheet"/}}
  #end
#end
{{/velocity}}</code>
