                 &lt;div class="row"&gt;
                    &lt;label&gt;$jobDoc.displayPrettyName("sourceChannelName")&lt;/label&gt;&lt;br/
                    $jobDoc.display("sourceChannelName", "edit", $candidateObj)
                 &lt;/div&gt;
                 &lt;div class="row"&gt;
                    &lt;label&gt;$jobDoc.displayPrettyName("office")&lt;/label&gt;&lt;br/&gt;
