                    &lt;label&gt;$jobDoc.displayPrettyName("sourceType")&lt;/label&gt;&lt;br/&gt;
                    $jobDoc.display("sourceType", "edit", $candidateObj)
                 &lt;/div&gt;
                 &lt;div class="row"&gt;
                    &lt;label&gt;$jobDoc.displayPrettyName("sourceChannelName")&lt;/label&gt;&lt;br/
