document.observe("xwiki:dom:loaded", function(){
    var updateClassTemplate = document.querySelector('#updateClassTemplate');
    var updateClassSheet = document.querySelector('#updateClassSheet');
    var updateClassTranslations = document.querySelector('#updateClassTranslations');
    
