(function(){
	document.observe('dom:loaded', function() {
		var jobPosting = $('RecruitmentApp.RecruitmentAppClass_0_jobPosting');
		if(jobPosting) {
			Event.observe(jobPosting, "focus", function(){
				new XWiki.widgets.Suggest(this, {
					script: "$xwiki.getURL('JobArea.AutoSuggest', 'get', 'spacename=JobArea&outputSyntax=plain&xpage=plain')&",
					varname: "input",
					seps: " ,|",
					offsety: 13
				});
			});
		}
	});
})();

(function(){
	document.observe('dom:loaded', function() {
		var jobPosting = $('RecruitmentApp.RecruitmentAppClass_0_assignee');
